  case 'verre'; indice=1.515*indice;bornes=[0,inf];ref='VERRE sans dispersion '; 
      
  case 'SF11';tmp=lambda.^2;bornes=[0,inf];ref='SF11 bornes a preciser';    
    indice=sqrt(1+ 1.73848403*tmp./(tmp-1.36068604e-2)+3.11168974e-1*tmp./(tmp-6.15960463e-2)+1.17490871*tmp./(tmp-1.21922711e+2));
    
